import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios"

export function Add (props) {
    // const {jobList, setJobList} = props;
    let emptyJob = { title: '', company: '', salary: '', dateApplied: '', response: false}
    const [job, setJob] = useState(emptyJob)
    const navigate = useNavigate()

    const handleChange = (event) => {
        setJob({...job, [event.target.name]: event.target.value})
    }
    const handleCreate = (addJob) => {
        axios.post('https://capstone-be.herokuapp.com/api/jobs', addJob).then((response)=>{
            console.log(response);
            console.log(response.data);
            // setJobList([...emptyJob, response.data]);

            // navigate('/all')
        }).catch((error)=>{
            console.log(error)
        })
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        props.handleCreate(addjob)
    }

    return (
        <div className="container text-bg-dark">
            <h1>Add Post</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input className="form-control" onChange = {handleChange}  id="title" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="company" className="form-label">Company</label>
                    <input className="form-control" onChange = {handleChange} id="company" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="salary" className="form-label">salary</label>
                    <input className="form-control" onChange = {handleChange} id="salary" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="dateApplied" className="form-label">Date Applied</label>
                    <input className="form-control" onChange = {handleChange} id="dateApplied" type="text"/>
                </div>

                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )


}
