import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios"

export function Edit(){

    const { id } = useParams()
    let emptyJob = { title: '', company: '', salary: '', dateApplied: '', response: false}
    const [jobId, setJobId] = useState(emptyJob)


    useEffect(()=>{
        console.log(id)
        axios.get(`https://capstone-be.herokuapp.com/api/jobs/${id}`)
        .then((response)=>{
            console.log(response.data);
            setJobId(response.data);
        })
        .catch((err)=>{console.log(err)})
    }, [id])


    const handleChange = (event) => {
        setJobId({...jobId, [event.target.name]: event.target.value})
    }

    const handleUpdate = (editJob) => {
        console.log(editJob)
        axios.put(`https://capstone-be.herokuapp.com/api/jobs/${id}`, editJob)
          .then((response) => {
            console.log(response)
            console.log(response.data)
          }).catch((error)=> {
            console.log(error)
          })
      }

      const handleSubmit = (event) => {
        event.preventDefault()
        handleUpdate(jobId)
    }

    return(
        <>
        <h1>
            Edit this Job
        </h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title: </label>
          <input
            type="text"
            name="title"
            value={jobId.title}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="company">Company: </label>
          <input
            type="text"
            name="company"
            value={jobId.company}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="salary">Salary: </label>
          <input
            type="text"
            name="salary"
            value={jobId.salary}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="dateApplied">Date Applied: </label>
          <input
            type="text"
            name="dateApplied"
            value={jobId.dateApplied}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="response">Response: </label>
          <input
            type="checkbox"
            name="response"
            value={jobId.response}
            onChange={handleChange}
          />
          <br/>
          <br/>
          <button><Link to='/'>Submit</Link></button>
        </form>
        </>
    )
}
