import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios"

export function Add () {
    const {jobList, setJobList} = props;
    const [title, setTitle] = useState('');
    const [company, setCompany] = useState('');
    const [salary, setSalary] = useState('');
    const [dateApplied, setDateApplied] = useState('');
    const [response, setResponse] = useState('');
    const navigate = useNavigate()
    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('https://capstone-be.herokuapp.com/api/jobs', {
            title,
            company,
            salary,
            dateApplied,
            response
        })
        .then((response)=>{
            console.log(response);
            console.log(response.data);
            setJobList([...jobList, response.data]);
            setTitle('');
            setCompany('');
            setSalary('');
            setDateApplied('');
            setResponse('')
            // navigate('/all')
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    // hi
    return (
        <div className="container text-bg-dark">
            <h1>Add Post</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input className="form-control" onChange={(event) => setTitle(event.target.value)} value={title} id="title" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="company" className="form-label">Company</label>
                    <input className="form-control" onChange={(event) => setCompany(event.target.value)} value={company} id="company" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="salary" className="form-label">salary</label>
                    <input className="form-control" onChange={(event) => setSalary(event.target.value)} value={salary} id="salary" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="dateApplied" className="form-label">Date Applied</label>
                    <input className="form-control" onChange={(event) => setDateApplied(event.target.value)} value={dateApplied} id="dateApplied" type="text"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="response" className="form-label">Response</label>
                    <input className="form-control" onChange={(event) => setResponse(event.target.value)} value={response} id="response" type="boolean"/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )


}
