import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios"

export function Edit(){


    let emptyJob = { title: '', company: '', salary: '', dateApplied: '', response: false}

    const [job, setJob] = useState(emptyJob)

    const handleChange = (event) => {
        setJob({...job, [event.target.name]: event.target.value})
    }

    const handleUpdate = (editJob) => {
        console.log(editJob)
        axios.put('https://capstone-be.herokuapp.com/api/jobs' + editJob.id, editJob)
          .then((response) => {
            console.log(response)
            console.log(response.data)
          }).catch((error)=> {
            console.log(error)
          })
      }

      const handleSubmit = (event) => {
        event.preventDefault()
        handleUpdate(job)
    }

    return(
        <>
        <h1>
            Edit this Job
        </h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title: </label>
          <input
            type="text"
            name="title"
            value={resolution.title}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="image">Image: </label>
          <input
            type="text"
            name="image"
            value={resolution.image}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="description">Description: </label>
          <input
            type="text"
            name="description"
            value={resolution.description}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="category">Category: </label>
          <input
            type="text"
            name="category"
            value={resolution.category}
            onChange={handleChange}
          />
          <br />
          <br />
          <label htmlFor="accomplished">accomplished: </label>
          <input
            type="checkbox"
            name="accomplished"
            value={resolution.accomplished}
            onChange={handleChange}
          />
          <br/>
          <br/>
          <input type="submit" />
        </form>
        </>
    )
}
