import { useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios"
export function Home(props) {
    const {job, setJob} = props;
    // const [job, setJob] = useState([])
    // const getJob = () => {
    //     axios.get('https://capstone-be.herokuapp.com/api/jobs').then((res) => {
    //         console.log(res.data)
    //         setJob(res.data)
    //     })
    // }
    useEffect(()=>{
        axios.get('https://capstone-be.herokuapp.com/api/jobs')
        .then((response)=> setJob(response.data), (err)=> console.log(err))
        .catch((error)=> console.log(error))
    }, [setJob])
return(
    <>
        {/* <h1>Ella's Job Hunt</h1>
            {job.map((job) => {
                return (
                    <div className='jobs' key={'job.id'}>
                        <h4><Link to = {`/${job._id}`}>{job.title}</Link></h4>
                        <h5>{job.company}</h5>
                     </div>
                )
            })} */}
            <div className="container" style={{backgroundColor: "#212529", height: "100vh", minHeight: "100vh"}}>
            <h1 className="text-bg-dark">Display All Jobs</h1>
            {job.map((post, key) => {
                return (
                    <div key={key} className="container bg-white text-bg-white  border border-secondary rounded border-3 mb-3" >
                        <div className="row">
                            <div className="col-md-8">
                                <div className="post-content">
                                    <div className="post-container">
                                        <div className="post-detail">
                                            <div className="user-info">
                                                <h6>Name Goes Here</h6>
                                            </div>
                                            {/* <div class="line-divider"></div> */}
                                            <div className="post-text">
                                                <h3>{post.title}</h3>
                                                <p>{post.company}</p>
                                                <Link to = {`/${job._id}`}>
                                                    <p>Edit/Delete</p>
                                                </Link>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )
            })}
        </div>
    </>
)
}
